CREATE TABLE schemawizard.posts (age INT NOT NULL, created_date DATETIME2 NOT NULL, id INT NOT NULL, rate DECIMAL DEFAULT 0.0 NOT NULL, title VARCHAR(300) NOT NULL, user_id INT NOT NULL, PRIMARY KEY (id), CONSTRAINT fk_posts_user_id FOREIGN KEY (user_id) REFERENCES schemawizard.users (id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT unq_title UNIQUE (title), CHECK (rate >= 0 AND rate <= 100), CONSTRAINT chk_user_age_greater_than_zero CHECK (age > 0))